mapper.select:
SELECT COUNT(*) FROM Community c JOIN "User" u ON u.user_id = c.user_id
AND c.category = #{category}
AND ( LOWER(c.title) LIKE LOWER('%' || #{q} || '%') OR LOWER(u.nickname) LIKE LOWER('%' || #{q} || '%') )

SELECT c.*, u.nickname
FROM Community c
JOIN "User" u ON c.user_id =
u.user_id


		AND u.nickname = #{nickname}


		AND c.title = #{title}

mapper.update:
UPDATE Community
title = #{title}, content = #{content}, image = #{image}, category = #{category},
WHERE community_id = #{communityId} AND user_id = #{userId}

mapper.delete:
DELETE FROM Community
WHERE community_id = #{communityId}
  AND user_id      = #{userId}
